package leetcode30DayChallenge.may;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class FindAllAnagramsInString {
	public List<Integer> findAnagrams(String s, String p) {
		// s: "cbaebabacd" p: "abc"

		if (s == null || s.length() == 0 || p.length() > s.length())
			return new ArrayList<>();

		List<Integer> res = new ArrayList<>();
		Map<Character, Integer> map = new HashMap<>();

		for (char c : p.toCharArray()) {
			map.put(c, map.getOrDefault(c, 0) + 1);
		}

		// steps
		// slide window for length of p
		// update map, check all entries of map are 0, if yes add the start
		// index to res.
		// keep moving start index.

		int end = 0;
		int begin = 0;
		int sLen = s.length(), pLen = p.length();
		int counter = map.size();

		while (end < sLen) {
			char c = s.charAt(end);
			if (map.containsKey(c)) {
				map.put(c, map.get(c) - 1);
				if (map.get(c) == 0)
					counter--;
			}
			end++;

			while (counter == 0) {
				char tempC = s.charAt(begin);
				if (map.containsKey(tempC)) {
					if (map.get(tempC) == 0)
						counter++;
					map.put(tempC, map.get(tempC) + 1);
				}

				if (end - begin == pLen) {
					res.add(begin);
				}

				begin++;

			}

		}

		return res;
	}

	public static void main(String[] args) {
//		System.out.println(new FindAllAnagramsInString().findAnagrams(
//				"",
//				""));
//		System.out.println(new FindAllAnagramsInString().findAnagrams("abacbabc", "abc"));
//		System.out.println(new FindAllAnagramsInString().findAnagrams("abab", "ab"));
		System.out.println(new FindAllAnagramsInString().findAnagrams("abaacbabc", "abc"));
		// "abaacbabc"
		// "abc"
	}

}
